import React, { useState, useEffect } from "react";
import { StyleSheet, View, ScrollView } from "react-native";
import { Button, TextInput } from "react-native-paper";
import { Dropdown } from "react-native-element-dropdown";

import livrosService from "../src/services/livros";
import emprestimoService from "../src/services/emprestimos";

export default function LoanAdd({ navigation }) {
  const [isFocus, setIsFocus] = useState(false);
  const [emprestimo, setEmprestimo] = useState({
    nome: "",
    contato: "",
    inicio: null,
    final: null,
    nome_livro: null,
  });

  const [livros, setLivros] = useState([]);

  useEffect(() => {
    const fetchLivros = async () => {
      const data = await livrosService.getAllLivros();
      setLivros(data);
    };
    fetchLivros();
  }, []);

  const save = async () => {
    const data = await emprestimoService.saveEmprestimo(emprestimo);
    navigation.goBack();
  };

  console.log("Livros:", livros); // Adicionado para verificar se livros estão sendo recuperados

  return (
    <ScrollView style={styles.container}>
      <View style={{ marginHorizontal: 10 }}>
        <TextInput
          label="Nome"
          style={{ marginBottom: 5 }}
          onChangeText={(text) =>
            setEmprestimo((emprestimo) => ({ ...emprestimo, nome: text }))
          }
        />
        <TextInput
          label="Contato"
          style={{ marginBottom: 5 }}
          onChangeText={(text) =>
            setEmprestimo((emprestimo) => ({ ...emprestimo, contato: text }))
          }
        />

        <Dropdown
          data={livros}
          labelField="nome"
          valueField="id"
          value={emprestimo.nome_livro}
          onChange={(item) => {
            console.log("Livro Selecionado:", item.nome); // Adicionado para verificar seleção de livro
            setEmprestimo((emprestimo) => ({ ...emprestimo, nome_livro: item.id }));
            setIsFocus(false);
          }}
        />
      </View>
      <View style={styles.buttons}>
        <Button mode="contained" onPress={save}>
          Adicionar
        </Button>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  image: {
    width: 200,
    height: 200,
    alignSelf: "center",
    marginBottom: 20,
  },
  dropdown: {
    height: 55,
    borderBottomColor: "#000a",
    borderBottomWidth: 0.8,
    borderTopRadius: 4,
    paddingHorizontal: 8,
  },
  placeholderStyle: {
    fontSize: 14,
    color: "#000a",
  },
  selectedTextStyle: {
    fontSize: 16,
  },
  inputSearchStyle: {
    height: 40,
    fontSize: 16,
  },
  buttons: {
    flexDirection: "row",
    justifyContent: "space-around",
    marginTop: 20,
    marginBottom: 20,
  },
});
